// AIOCHI Threat Narrative Generator
// Transforms raw security events into human-readable stories

const event = items[0].json;

// Threat type to friendly name mapping
const threatNames = {
  'malware': 'malicious software',
  'phishing': 'phishing attempt',
  'port_scan': 'port scanning activity',
  'brute_force': 'password attack',
  'dns_tunneling': 'suspicious DNS activity',
  'c2_beacon': 'command & control communication',
  'data_exfil': 'data exfiltration attempt',
  'ddos': 'denial of service attack',
  'sql_injection': 'database attack',
  'xss': 'script injection attempt'
};

// Severity to icon/color mapping
const severityStyles = {
  'critical': { icon: 'fa-skull-crossbones', color: 'danger', emoji: 'üö®' },
  'high': { icon: 'fa-exclamation-triangle', color: 'warning', emoji: '‚ö†Ô∏è' },
  'medium': { icon: 'fa-info-circle', color: 'info', emoji: 'üì¢' },
  'low': { icon: 'fa-check-circle', color: 'success', emoji: '‚úÖ' }
};

// Get readable threat name
const threatType = event.threat_type || event.event_type || 'unknown threat';
const friendlyThreat = threatNames[threatType] || threatType.replace(/_/g, ' ');

// Get severity styling
const severity = (event.severity || 'medium').toLowerCase();
const style = severityStyles[severity] || severityStyles.medium;

// Get device info if available
const deviceName = event.device_name || event.device_label || 'a device';
const sourceIp = event.source_ip || 'unknown source';

// Build narrative based on action taken
let narrative = '';
let title = '';

if (event.action === 'blocked' || event.blocked) {
  title = 'Threat Blocked';
  narrative = `I detected and blocked ${friendlyThreat} targeting ${deviceName}. `;
  narrative += `The attack came from ${sourceIp}. Your network is safe!`;
} else if (event.action === 'allowed' || event.action === 'monitored') {
  title = 'Activity Monitored';
  narrative = `I noticed ${friendlyThreat} activity involving ${deviceName}. `;
  narrative += `I'm keeping an eye on it. The traffic pattern seemed unusual.`;
} else {
  title = 'Security Event';
  narrative = `I detected ${friendlyThreat} related to ${deviceName}. `;
  narrative += `I've logged this event for analysis.`;
}

// Build the narrative event
const narrativeEvent = {
  id: `threat-${Date.now()}`,
  timestamp: event.timestamp || new Date().toISOString(),
  category: 'security',
  severity: severity,
  icon: style.icon,
  color: style.color,
  emoji: style.emoji,
  title: title,
  narrative: narrative,
  device: deviceName,
  source_ip: sourceIp,
  threat_type: threatType,
  raw_event_id: event.id || event.event_id
};

return [{ json: narrativeEvent }];
